<?phpnamespace App\AdminModule\Presenters;use App\AdminModule\Forms\AddNewsFormFactory;use App\AdminModule\Forms\EditNewsFormFactory;class NewsPresenter extends BasePresenter{    /** @var AddNewsFormFactory @inject */    public $addNewsFF;        /** @var EditNewsFormFactory @inject */    public $editNewsFF;        public function renderDefault(){        $this->template->news = $this->news->getNews();        $this->template->approvedNews = $this->news->getApprovedNews();        $this->template->unApprovedNews = $this->news->getUnapprovedNews();        $this->template->editations = $this->news->getNewsEditations();    }        public function renderShowNews($newsId){        $this->template->article = $this->news->getSingleNews($newsId);        $this->template->pictures = $this->news->getAdditionalImages($newsId);        if($this->news->editationExists($newsId)){            $this->template->editation = $this->news->getNewsEditation($newsId);            $this->flashMessage('Tato novinka má neschválené úpravy!', 'warning');        }    }        public function renderEditNews($newsId){    }        public function actionApproveNews($newsId){        $this->news->approveNews($newsId);        $this->flashMessage('Příspěvek úspěšně schválen.');        $this->redirect('News:default');            }        public function actionUnapproveNews($newsId){        $this->news->unApproveNews($newsId);        $this->flashMessage('Příspěvek úspěšně přidržen.');        $this->redirect('News:default');    }        public function actionDeleteNews($newsId){        $this->news->deleteNews($newsId, $this->context->parameters['wwwDir']);        $this->flashMessage('Příspěvek úspěšně smazán.');        $this->redirect('News:default');    }        public function actionApproveChanges($newsId){        $this->news->approveChanges($newsId);        $this->flashMessage('Úpravy novinky byly publikovány.');        $this->redirect('News:default');    }        public function createComponentAddNewsForm(){        return $this->addNewsFF->create($this->context->parameters['wwwDir']);    }        public function createComponentEditNewsForm(){        return $this->editNewsFF->create($this->getParameter('newsId'));    }}