<?phpnamespace App\AdminModule\Presenters;use App\AdminModule\Forms\AddContentFormFactory;use App\AdminModule\Forms\EditContentFormFactory;class SectionsPresenter extends BasePresenter{        /** @var AddContentFormFactory @inject */    public $addContentFormFactory;        /** @var EditContentFormFactory @inject */    public $editContentFormFactory;        public function renderDefault(){        $this->template->sections = $this->sections->getSections();    }        public function renderShow($sectionId){        $this->template->section = $this->sections->getSection($sectionId);        $this->template->sectionContent = $this->sections->getSectionContent($sectionId);    }        public function renderShowPost($postId){        $this->template->post = $this->sections->getContent($postId);        $this->template->pictures = $this->sections->getAdditionalImages($postId);    }        public function renderAddPost($sectionId){    }        public function renderEditPost($contentId){    }        public function actionDeletePost($contentId){        $this->sections->deleteContent($contentId, $this->context->parameters['wwwDir']);        $this->flashMessage('Příspěvek úspěšně smazán.', 'success');        $this->redirect('Sections:default');    }        public function createComponentAddContentForm(){        return $this->addContentFormFactory->create($this->getParameter('sectionId'));    }        public function createComponentEditContentForm(){        return $this->editContentFormFactory->create($this->getParameter('contentId'));    }}