<?phpnamespace App\Presenters;class HomepagePresenter extends BasePresenter{    public function renderDefault($page = 1){        $paginator = new \Nette\Utils\Paginator();        $paginator->setItemCount(count($this->news->getUnPinnedNews()));        $paginator->setItemsPerPage(5);        $paginator->setPage($page);                $this->template->paginator = $paginator;        $this->template->pinnedNews = $this->news->getPinnedNews();        $this->template->unPinnedNews = $this->news->getUnPinnedNewsForPaginator($paginator->getLength(), $paginator->getOffset());    }        public function renderShowNews($newsId){        $this->template->news = $this->news->getSingleNews($newsId);        $this->template->pictures = $this->news->getAdditionalImages($newsId);    }}