<?phpnamespace App\Presenters;class SectionsPresenter extends BasePresenter{    public function renderShow($sectionId, $page = 1){        $paginator = new \Nette\Utils\Paginator();        $paginator->setItemCount(count($this->sections->getSectionContent($sectionId)));        $paginator->setItemsPerPage(5);        $paginator->setPage($page);        $this->template->paginator = $paginator;                $this->template->section = $this->sections->getSection($sectionId);        $this->template->posts = $this->sections->getSectionContentForPaginator($sectionId, $paginator->getLength(), $paginator->getOffset());    }        public function renderShowPost($postId){        $this->template->post = $this->sections->getContent($postId);        $this->template->pictures = $this->sections->getAdditionalImages($postId);    }}